mapper.select:

SELECT
	i.id,i.order_id as orderId,i.create_date as createDate, i.modify_date as modifyDate, i.stock,i.department,i.staff, i.number,i.worth
FROM 
	OUT_STOCK i
WHERE 
 	i.number>0

 		  and i.create_date>= #{beginTime} 


 		  and  i.create_date<= #{endTime}  


 		  and i.department= #{department}  


 		  and i.staff=#{staff} 


 		  and i.stock=#{stock}


SELECT
	count(*)
FROM 
	OUT_STOCK i
WHERE 
 	i.number>0

 		  and i.create_date>= #{beginTime} 


 		  and  i.create_date<= #{endTime}  


 		  and i.department= #{department}  


 		  and i.staff=#{staff} 


 		  and i.stock=#{stock}
