mapper.select:
SELECT
	o.ID,o.create_date as createDate,o.modify_date as modifyDate,o.department,o.staff,o.total_number as totalNumber,o.total_worth as totalWorth,status,mark
FROM 
	stock_order o
WHERE 
	o.total_number>0 and 
	status like '%in%'  

 		  and o.create_date>= #{beginTime} 


 		  and  o.create_date<= #{endTime}  


 		  and  o.department<= #{department}  


 		  and  o.id= #{orderId}  

 	order by o.id desc

SELECT
	count(*)
FROM 
	stock_order o
WHERE 
	o.total_number>0 and 
	status like '%in%'  

 		  and o.create_date>= #{beginTime} 


 		  and  o.create_date<= #{endTime}  


 		  and  o.department<= #{department}  


 		  and  o.id= #{orderId}

